mapper.insert:
insert into InquiryBoard (
IBoardSubject, IBoardContent,
IBoardWriter, IBoardDate, IBoardType, UserEmail, UserPhoneNum, UserID, State
) values(
#{IBoardSubject},
#{IBoardContent},
#{IBoardWriter}, #{IBoardDate}, #{IBoardType}, #{UserEmail}, #{UserPhoneNum}, #{UserID}, #{State}
)

mapper.select:
select IBoardID, IBoardSubject, IBoardWriter, IBoardDate, IBoardContent, UserID, IBoardAnswer
from InquiryBoard 
where IBoardID = #{IBoardID}

select UserID
from User 
where UserLoginID = #{loginID}

mapper.update:
UPDATE InquiryBoard 
SET IBoardDelete = 1
where IBoardID = #{IBoardID}

UPDATE InquiryBoard 
SET IBoardAnswer = #{IBoardAnswer},
	State = #{State}
where IBoardID = #{IBoardID}
