TeamCreationDate, ClassID
) values(
#{TeamName},
#{TeamLeaderID},#{TeamLeaderName},
#{TeamCreationDate}, #{ClassID}
)

mapper.select:
select *
from TeamFile
where TBoardSubject = #{TBoardSubject}

select *
from Class
where ClassID = #{ClassID}

